// Client-side POST to Node/PHP if configured; otherwise Netlify processes the form
document.getElementById('contact-form')?.addEventListener('submit', async (e)=>{
  const form = e.target;
  const data = new FormData(form);
  const payload = Object.fromEntries(data.entries());
  const status = document.getElementById('contact-status');
  try{
    // Try your own API first (set CONTACT_API to '/api/contact' or your endpoint)
    const CONTACT_API = window.CONTACT_API || '';
    if(CONTACT_API){
      e.preventDefault();
      const res = await fetch(CONTACT_API, {method:'POST', headers:{'Content-Type':'application/json'}, body: JSON.stringify(payload)});
      const j = await res.json();
      if(j.ok){ status.textContent = 'Merci ! Votre message a été envoyé.'; form.reset(); }
      else { status.textContent = 'Erreur: ' + (j.error || 'envoi impossible'); }
    }
  }catch(err){
    status.textContent = 'Erreur réseau.';
  }
});

// Pricing logic
  const PRICING = {
    monthly: {1: 9.99, 2: 14.99, 3: 18.99},
    annualDiscount: 0.17, // 17%
    promoCode: "PROTEGEMOI2026",
    promoDiscount: 0.20 // 20%
  };

  const plansDiv = document.getElementById('plans');
  const toggle = document.getElementById('freqToggle');
  const label = document.getElementById('freqLabel');
  const promoInput = document.getElementById('promoInput');
  const promoStatus = document.getElementById('promoStatus');
  const applyBtn = document.getElementById('applyPromoBtn');

  let useAnnual = false;
  let promoApplied = false;

  function formatEUR(n){ return n.toFixed(2).replace('.', ',') + ' € / mois'; }

  function priceFor(kids){
    const base = PRICING.monthly[kids]; // per month
    if(!useAnnual){
      let p = base;
      if (promoApplied) p = p * (1 - PRICING.promoDiscount);
      return p;
    }else{
      // annual equivalent per month with 17% off
      let p = base * (1 - PRICING.annualDiscount);
      if (promoApplied) p = p * (1 - PRICING.promoDiscount);
      return p;
    }
  }

  function originalPriceFor(kids){
    // For strike-through: price without promo but with annual if annual mode
    const base = PRICING.monthly[kids];
    return useAnnual ? base * (1 - PRICING.annualDiscount) : base;
  }

  function render(){
    label.textContent = useAnnual ? '(Annuel -17%)' : '(Mensuel)';
    plansDiv.innerHTML = [1,2,3].map(kids => {
      const current = priceFor(kids);
      const original = originalPriceFor(kids);
      const strike = promoApplied ? `<span class="strike">${formatEUR(original)}</span>` : '';
      return `
        <div class="card">
          <div class="plan-badge">${kids} enfant${kids>1?'s':''}</div>
          <div class="price">${strike}${formatEUR(current)}</div>
          <p class="small">Accès complet ProtègeMoi pour ${kids} appareil${kids>1?'s':''} enfant.</p>
          <a class="btn primary" href="contact.html">Souscrire</a>
        </div>`;
    }).join('');
  }

  toggle.addEventListener('change', ()=>{ useAnnual = toggle.checked; render(); });
  applyBtn.addEventListener('click', ()=>{
    const code = promoInput.value.trim();
    if(code.toUpperCase() === PRICING.promoCode){
      promoApplied = true;
      promoStatus.textContent = 'Code promo appliqué (-20%)';
    } else {
      promoApplied = false;
      promoStatus.textContent = 'Code invalide';
    }
    render();
  });

  render();
