function getLangFromURL(){const e=new URLSearchParams(window.location.search);return e.get('lang')||'fr'}async function loadLanguage(e){try{const n=await fetch(`locales/${e}.json`),t=await n.json();applyTranslations(t)}catch(e){console.error('Erreur chargement langue :',e)}}function applyTranslations(e){document.querySelector("[data-i18n='hero.slogan']").textContent=e.hero.slogan,document.querySelector("[data-i18n='hero.subtitle']").textContent=e.hero.subtitle,document.querySelector("[data-i18n='hero.cta_demo']").textContent=e.hero.cta_demo,document.querySelector("[data-i18n='hero.cta_enter']").textContent=e.hero.cta_enter,document.querySelector("[data-i18n='about.title']").textContent=e.about.title,document.querySelector("[data-i18n='about.text']").textContent=e.about.text,document.querySelector("[data-i18n='features.title']").textContent=e.features.title,document.querySelector("[data-i18n='features.detection.title']").textContent=e.features.list.detection.title,document.querySelector("[data-i18n='features.detection.desc']").textContent=e.features.list.detection.desc,document.querySelector("[data-i18n='features.alert.title']").textContent=e.features.list.alert.title,document.querySelector("[data-i18n='features.alert.desc']").textContent=e.features.list.alert.desc,document.querySelector("[data-i18n='features.stealth.title']").textContent=e.features.list.stealth.title,document.querySelector("[data-i18n='features.stealth.desc']").textContent=e.features.list.stealth.desc,document.querySelector("[data-i18n='features.privacy.title']").textContent=e.features.list.privacy.title,document.querySelector("[data-i18n='features.privacy.desc']").textContent=e.features.list.privacy.desc,document.querySelector("[data-i18n='how.title']").textContent=e.how.title;document.querySelectorAll("[data-i18n^='how.steps']").forEach((n,t)=>n.textContent=e.how.steps[t]);document.querySelector("[data-i18n='email.title']").textContent=e.email.title,document.querySelector("[data-i18n='email.subtitle']").textContent=e.email.subtitle,document.querySelector("[data-i18n='email.placeholder']").setAttribute('placeholder',e.email.placeholder),document.querySelector("[data-i18n='email.button']").textContent=e.email.button,document.querySelector("[data-i18n='email.note']").textContent=e.email.note,document.querySelector("[data-i18n='footer.rights']").textContent=e.footer.rights,document.querySelector("[data-i18n='footer.links.legal']").textContent=e.footer.links.legal,document.querySelector("[data-i18n='footer.links.privacy']").textContent=e.footer.links.privacy,document.querySelector("[data-i18n='footer.links.terms']").textContent=e.footer.links.terms,document.querySelector("[data-i18n='footer.links.contact']").textContent=e.footer.links.contact}const lang=getLangFromURL();loadLanguage(lang);